import {camelCase, isFunction} from 'lodash-bound';
import assert                  from 'power-assert';
import {Observable}            from './libs/expect-rxjs.js';
import $                       from './libs/jquery.js';
import Machine                 from './util/Machine';
import {Point2D}               from './util/svg';
import {ValueTracker}          from 'utilities';

const $$domEvents   = Symbol('$$domEvents');
const $$tools       = Symbol('$$tools');
const $$initialized = Symbol('$$initialized');


/**
 * The central hub of the artefacts and tools of a boxer canvas.
 * Creating one is among the first steps in using the boxer library.
 */
export class Coach extends ValueTracker {

	/**
	 * the main state machine toggling between `'IDLE'` and `'BUSY'`,
	 * used to coordinate between the local state machines of tools
	 * @type {Machine}
	 */
	stateMachine = new Machine('Coach', { state: 'IDLE' });

	/**
	 * Create a new `Coach` instance rooted at a specific `Canvas` artefact.
	 * @param options
	 * @param {Canvas} options.root - the canvas making up the root of our boxer svg artefact tree
	 */
	constructor(options) {
		super();
		const {root} = options;
		this.root = root;
		this[$$domEvents] = {};

		// /* keep track of canvas offset */
		// this._offset = {
		// 	left: 0,
		// 	top: 0
		// };
		// setInterval(() => {
		// 	this._offset = this.root.svg.main.offset();
		// }, 1000);
	}

	/**
	 * Register a new tool. Assumes that the coach has not yet started.
	 * @param {Tool} tool - the new tool
	 * @return {Coach} this coach object
	 */
	addTool(tool) {
		assert(!this[$$initialized], "You can't add tools to a Coach after starting it.");
		this[tool.constructor.name::camelCase()] = tool;
		if (!this[$$tools]) { this[$$tools] = new Set }
		this[$$tools].add(tool);
		tool.init({ coach: this });
		return this;
	}

	/**
	 * Activate a given set of registered tools and turn off all others.
	 * @param {Iterable<Tool>} chosenTools - the tools to activate
	 */
	activateExclusiveTools(chosenTools) {
		chosenTools = [...chosenTools];
		for (let tool of this[$$tools]) {
			let makeActive =
				chosenTools.includes(tool)                  ||
				chosenTools.includes(tool.constructor)      ||
				chosenTools.includes(tool.constructor.name) ||
				chosenTools.includes(tool.constructor.name::camelCase());
			if (makeActive !== tool.active) {
				tool.active = makeActive;
			}
		}
	}

	/**
	 * Start this coach, indicating that all tools have been registered.
	 */
	start() {
		this[$$initialized] = true;
		for (let tool of this[$$tools]) {
			if (tool.postInit::isFunction()) {
				tool.postInit({ coach: this });
			}
		}
	}

	/**
	 * Let the coach know that it should start listening to specific types
	 * of DOM events on artefacts, and to provide a way to subscribe to them.
	 * It uses jQuery's delegation feature, so it is relatively efficient,
	 * and it normalizes event objects and supplies them with additional
	 * quick-access data, like the mouse event coordinates as a `Point2D`.
	 * @param {Array<string>} events - a set of DOM event names, e.g., `'click'`, `'mouseover'`
	 */
	registerArtefactEvent(...events) {
		for (let e of events) {
			if (!this[$$domEvents][e]) {
				this[$$domEvents][e] = Observable.merge(
					Observable.fromEventPattern(
						(fn) => { $(this.root.svg.main).on (e, '.boxer > .handles *', fn) },
						(fn) => { $(this.root.svg.main).off(e, '.boxer > .handles *', fn) }
					),
					Observable.fromEventPattern(
						(fn) => { $(this.root.svg.main).on (e, fn) },
						(fn) => { $(this.root.svg.main).off(e, fn) }
					)
				).do(::this.enrichMouseEvent);
			}
		}
	}

	/**
	 * Enrich a mouse event object with a `Point2D` object representing
	 * screen coordinates.
	 * @private
	 * @param {jQuery.Event} event - a jQuery event object
	 */
	enrichMouseEvent(event) {
		event.stopPropagation();
		event.point = new Point2D({
			x:                event.pageX,
			y:                event.pageY,
			coordinateSystem: this.root.svg.main
		});
	}

	/**
	 * @param {String} e - the type of event to get a stream of
	 * @returns {Observable} a stream of `e` events on the root canvas element
	 */
	rootE(e) { return Observable.fromEvent($(this.root.svg.main), e).do(::this.enrichMouseEvent) }

	/**
	 * @param {String} e - the type of event to get a stream of
	 * @returns {Observable} a stream of `e` events on the `window` DOM element
	 */
	windowE(e) { return Observable.fromEvent($(window), e).do(::this.enrichMouseEvent) }

	/**
	 * @param {String} e - the type of event to get a stream of
	 * @returns {Observable} a stream of `e` events on the `document` DOM element
	 */
	documentE(e) { return Observable.fromEvent($(document), e).do(::this.enrichMouseEvent) }

	/**
	 * @param {String} e - the type of event to get a stream of
	 * @returns {Observable} a stream of `e` events on any artefact
	 */
	e(e) { return this[$$domEvents][e] }

}

/**
 * @return {SvgArtefact} the artefact controller in charge of a given SVG DOM element
 * @param {SVGElement} element - the element for which to get the controller
 */
export function elementController(element) {
	element = $(element instanceof $.Event ? element.target : element); // take an event or an element
	let controller = null;
	do {
		controller = element.data('boxer-controller');
		element = element.parent();
	} while (!controller && element.length > 0);
	return controller;
}


/** @private */
export function getHandler(key) {
	if (this) { return this.handlers[key] }
	return (artefact) => artefact.handlers[key];
}

/**
 * Take an event stream and transform it into a stream of handler information
 * for only specifically requested handler types.
 * @this {Observable<jQuery.Event>}
 * @returns {Observable<Object>} information on relevant boxer handlers
 * @param {string} key - the handler type we're interested in, e.g., `'clickable'`, or `'*'` for all types
 */
export function handleBoxer(key) {
	return this
		.map((event)=> {
			if (event instanceof $.Event) {
				return [event.point, elementController(event), $(event.target).data('boxer-handlers')[key]];
			} else if (event) {
				const controller = event.artefact;
				return [event.point, controller, controller.handlers[key]];
			}
		})
	    .filter(v => key === '*' || !!v[2])
		.map(([point, controller, handlers]) => {
			let originalArtefact = controller;
			handlers = (key === '*' ? {} : handlers);
			return {
				...handlers,
				handlerType: key,
				originalArtefact,
				artefact: handlers && handlers.artefact || originalArtefact,
				point
			}
		});
}

import assert from 'power-assert';
import $      from '../libs/jquery.js';
import {entries, isEmpty, isArray, pull, values} from 'lodash-bound';
import {Observable} from '../libs/expect-rxjs.js';

import {ID_MATRIX, Point2D} from '../util/svg.js';
import {property, flag, definePropertiesByValue} from 'utilities';
import {_isNonNegative} from '../util/misc.js';

import {SvgTransformable} from './SvgTransformable.js';
import {BoxCorner}        from './BoxCorner.js';
import {MX, MY, setCTM} from '../util/svg';
import {BoxBorder} from './BoxBorder';
import {Glyph} from './Glyph';

const {max} = Math;

export const BORDER_WIDTH = 2;
export const MIN_MIN_SIZE = 2*max(BoxCorner.RADIUS, BORDER_WIDTH);

/**
 * Representation of an interactive rectangle in svg space.
 */
export class Box extends SvgTransformable {

	@property({ isValid: _isNonNegative, initial: MIN_MIN_SIZE }) minWidth;
	@property({ isValid: _isNonNegative, initial: MIN_MIN_SIZE }) minHeight;

	@property({ isValid: _isNonNegative, initial: MIN_MIN_SIZE, transform(w) { return max(w || MIN_MIN_SIZE, this.minWidth  || MIN_MIN_SIZE) } }) width;
	@property({ isValid: _isNonNegative, initial: MIN_MIN_SIZE, transform(h) { return max(h || MIN_MIN_SIZE, this.minHeight || MIN_MIN_SIZE) } }) height;

	@property({ initial: {} }) stuckBorders; // TODO: more elegant solution


	getSvgContainerFor(artefact) {
		// TODO: if still needed, move this functionality to the dropzone handler
		if (artefact instanceof BoxBorder || artefact instanceof BoxCorner) {
			return this.svg.outline;
		}
		// return this.svg.content;
		return this.svg.children;
	}

	preCreate(options) {
		super.preCreate(options);

		/* set width/height if given */
		if (options.width)  { this.width  = options.width  }
		if (options.height) { this.height = options.height }

		/* keep width/height at or above minimum */
		for (let [ minKey    ,  key    ] of [
			     ['minWidth' , 'width' ],
			     ['minHeight', 'height']
		]) {
			this.p([minKey], [key])
			    .filter(([m, v]) => v < m)
			    .map   (([m, v]) => m    )
			    .subscribe( this.p(key) );
		}
	}

	create(options = {}) {
		super.create(options);

		this.svg.outline = $.svg('<g class="outline">')
			.css(this.constructor.inheritedProperties)
			.insertAfter(this.svg.children);

		const handlePath = $.svg('<path>').attr({
			rx:      BoxCorner.RADIUS,
			ry:      BoxCorner.RADIUS,
			stroke: 'none',
			fill:   'none'
		}).appendTo(this.svg.handles);

		const inkPath = $.svg('<path>').attr({
			rx:      BoxCorner.RADIUS,
			ry:      BoxCorner.RADIUS,
			stroke:  'transparent',
			fill:    'inherit'
		}).appendTo(this.svg.ink);

		const overlayPath = $.svg('<path>').attr({
			stroke:           'inherit',
			strokeWidth:      'inherit',
			strokeDasharray:  'inherit',
			strokeDashoffset: 'inherit',
			fill:             'transparent',
		}).appendTo(this.svg.overlay);

		/* borders */
		this.borders = {};
		for (let [key, x, y] of [
			['top',    0, -1],
			['right', +1,  0],
			['bottom', 0, +1],
			['left',  -1,  0]
		]) {
			this.borders[key] = new BoxBorder({
				parent: this,
				lengthen1: -BoxCorner.RADIUS,
				lengthen2: -BoxCorner.RADIUS,
				side:      {key, x, y}
			});
			this.borders[key].registerHandlers({
				resizable: {
					artefact: this,
					directions: {x, y},
					before: () => { this.handlesActive = false },
					after:  () => { this.handlesActive = true  }
				},
				highlightable: {
					artefact: this,
					effect: {
						elements: this.borders[key].svg.overlay
					}
				},
				dropzone: {
					artefact: this.borders[key],
					after: ({artefact}) => {
						if (artefact instanceof Box) {
							// TODO: finish this (mirror the LyphBox.js version)
						} else if (artefact instanceof Glyph) {
							artefact.parent = this.borders[key]
						}
					}
				}
			});
		}

		/* corners */
		this.corners = {};
		for (let [key, x, y, s1, s2] of [
			['tl', -1, -1, 'top',    'left' ],
			['tr', +1, -1, 'top',    'right'],
			['bl', -1, +1, 'bottom', 'left' ],
			['br', +1, +1, 'bottom', 'right']
		]) {
			this.corners[key] = new BoxCorner({
				parent: this
			});
			this.corners[key].registerHandlers({
				resizable: {
					artefact: this,
					directions: {x, y},
					before: () => { this.handlesActive = false },
					after:  () => { this.handlesActive = true  }
				},
				highlightable: {
					artefact: this,
					effect: {
						elements: this.corners[key].svg.overlay
							  .add(this.borders[s1].svg.overlay)
							  .add(this.borders[s2].svg.overlay)
					}
				}
			});
		}

		/* better corner accessibility */
		this.corners::definePropertiesByValue({
			top:    { left: this.corners.tl, right:  this.corners.tr },
			right:  { top:  this.corners.tr, bottom: this.corners.br },
			bottom: { left: this.corners.bl, right:  this.corners.br },
			left:   { top:  this.corners.tl, bottom: this.corners.bl }
		});

		/* bookkeeping */
		let cornerPoints = {
			tl: { x: -1, y: -1, r:   0 },
			tr: { x: +1, y: -1, r:  90 },
			br: { x: +1, y: +1, r: 180 },
			bl: { x: -1, y: +1, r: 270 },
		};
		let borderPoints = {
			top:    [cornerPoints.tl, cornerPoints.tr],
			right:  [cornerPoints.tr, cornerPoints.br],
			bottom: [cornerPoints.br, cornerPoints.bl],
			left:   [cornerPoints.bl, cornerPoints.tl]
		};

		/* keep outline updated */
		Observable.combineLatest(
			this.p('width'),
			this.p('height'),
			this.corners.tr.p('rounded'),
			this.corners.br.p('rounded'),
			this.corners.bl.p('rounded'),
			this.corners.tl.p('rounded')
		).subscribe(([w, h]) => {
			/* place the four corners */
			for (let [key, cp] of cornerPoints::entries()) {
				cp.p = new Point2D({
					x:                cp.x * w / 2,
					y:                cp.y * h / 2,
					coordinateSystem: this.svg.outline
				});
				this.corners[key].transformation = ID_MATRIX.translate(...cp.p.xy).rotate(cp.r);
			}
			/* place the borders */
			for (let [key, [{p:p1}, {p:p2}]] of borderPoints::entries()) {
				this.borders[key].point1 = p1.in(this.svg.outline);
				this.borders[key].point2 = p2.in(this.svg.outline);
			}

			/* generate outline */
			const top1    = this.borders.top   .inkPoint1;
			const top2    = this.borders.top   .inkPoint2;
			const right1  = this.borders.right .inkPoint1;
			const right2  = this.borders.right .inkPoint2;
			const bottom1 = this.borders.bottom.inkPoint1;
			const bottom2 = this.borders.bottom.inkPoint2;
			const left1   = this.borders.left  .inkPoint1;
			const left2   = this.borders.left  .inkPoint2;
			const cornerPath = (key) => {
				const c = this.corners[key];
				const s = BoxCorner.RADIUS;
				const {x, y} = cornerPoints[key];
				if (c.rounded) {
					return `A ${s} ${s}, 0, 0, 0,`;
				} else {
					if (x*y === -1) { return `v ${y*s} L` }
					else            { return `h ${x*s} L` }
				}
			};
			$().add(inkPath)
			   .add(handlePath)
			   .add(overlayPath).attr({
				d: `M
					${left1.xy}   L ${left2.xy}   ${cornerPath('bl')}
					${bottom1.xy} L ${bottom2.xy} ${cornerPath('br')}
					${right1.xy}  L ${right2.xy}  ${cornerPath('tr')}
					${top1.xy}    L ${top2.xy}    ${cornerPath('tl')}
					${left1.xy}
				Z`
			});
		});

		/* when parent changes, 'unstuck' all borders */
		this.p('parent').subscribe(() => {
			this.stuckBorders = {};
		});






		// this.p('stuckBorders').switchMap((stb) => {
		//
		// 	console.log('----', stb);
		//
		// 	if (!stb.left && !stb.right && !stb.top && !stb.bottom) {
		// 		return Observable.never();
		// 	}
		//
		// 	let streams = {};
		//
		// 	for (let side of ['left', 'right']) {
		// 		if (stb[side]) {
		// 			const {box, relation, x} = stb[side];
		// 			if (relation === 'parent') {
		// 				streams[side] = box.p('width').map(w => x * w/2);
		// 			} else { // sibling
		// 				streams[side] = box.p(['width', 'transformation']).map(([w, t]) => t[MX] - x * w/2);
		// 			}
		// 		} else {
		// 			streams[side] = Observable.of(null);
		// 		}
		// 	}
		//
		//
		//
		// 	// TODO: top, bottom
		//
		//
		//
		// 	const thisWX = this.p(['width', 'transformation']).map(([w, t]) => [w, t[MX]]);
		// 	const thisL  = thisWX.map(([w, x]) => x - w/2);
		// 	const thisR  = thisWX.map(([w, x]) => x + w/2);
		// 	return Observable
		// 		.combineLatest(streams.left, streams.right)
		// 		.withLatestFrom(thisL, thisR, ([l, r], tl, tr) => {
		// 			console.log(l, r, tl, tr);
		// 			if (l === null) { l = tl }
		// 			if (r === null) { r = tr }
		// 			return {
		// 				x:    (l + r) / 2,
		// 				width: r - l
		// 			};
		// 		});
		// }).subscribe(({x, width}) => {
		// 	console.log(x, width);
		// 	this.transformation = ID_MATRIX.translate(x, this.transformation[MY]);
		// 	this.width = w;
		// });





		/* react to parent resizing when stuck to border */
		this.p(['width', 'height', 'parent.width', 'parent.height', 'stuckBorders']).subscribe(([w, h, pw, ph, stb]) => {
			stb = [...stb::values()];
			if (!stb || stb.length === 0) { return }
			const stX = [...new Set(stb.map(({x}) => x))];
			const stY = [...new Set(stb.map(({y}) => y))];
			if (stX.length > 1) { stX::pull(0) }
			if (stY.length > 1) { stY::pull(0) }
			if (stX.length === 2) { w = pw }
			if (stY.length === 2) { h = ph }
			const [x, y] = [stX[0], stY[0]];
			const oldX = this.transformation[MX];
			const oldY = this.transformation[MY];
			this.transformation = ID_MATRIX.translate(!x * oldX + x*(pw-w)/2, !y * oldY + y*(ph-h)/2).rotate(0);  // TODO
			this.width  = w;
			this.height = h;
		});

		/* when stuck to borders, deactivate the appropriate outline handles */
		this.p('stuckBorders').subscribe((stb) => {
			// borders
			this.borders.left.handlesActive   = !stb.left;
			this.borders.right.handlesActive  = !stb.right;
			this.borders.top.handlesActive    = !stb.top;
			this.borders.bottom.handlesActive = !stb.bottom;
			// corners
			this.corners.tl.handlesActive = !stb.top    && !stb.left;
			this.corners.tr.handlesActive = !stb.top    && !stb.right;
			this.corners.bl.handlesActive = !stb.bottom && !stb.left;
			this.corners.br.handlesActive = !stb.bottom && !stb.right;
		});

	}

	postCreate(options = {}) {
		/* set standard handlers */
		this.registerHandlers({
			movable:   {
				artefact: this,
				after: () => {
					/* when dropped, reapply relevant border stucknesses */
					this.stuckBorders = {...this.stuckBorders};
				}
			},
			rotatable: {
				artefact: this,
				after: () => {
					/* after rotating, reapply relevant border stucknesses */
					//  which, if present, would undo rotation
					this.stuckBorders = {...this.stuckBorders};
					// TODO: simply disallow rotation when stuck
				}
			},
			dropzone:  {
				artefact: this,
				after: ({artefact}) => {
					artefact.parent = this;
				}
			},
			drawzone: {
				artefact: this,
				accepts({ artefact }) {
					return artefact instanceof SvgTransformable;
				}
			},
			highlightable: {
				artefact: this,
				effect: { elements: this.svg.overlay }
			},
			deletable: {
				artefact: this
			}
		});

		/***/
		super.postCreate(options);
	}

}

import assert from 'power-assert';
import $      from '../libs/jquery.js';
import {isBoolean as _isBoolean} from 'lodash';
import {entries, values, isUndefined} from 'lodash-bound';

import {ID_MATRIX, SVGMatrix, setCTM, Point2D} from '../util/svg.js';
import {property, flag} from 'utilities';


import {LineSegment} from './LineSegment.js';

/**
 * Representation of a Box border.
 */
export class BoxBorder extends LineSegment {

	side: ?{key: 'top',    x:  0, y: -1}
		 | {key: 'bottom', x:  0, y:  1}
		 | {key: 'left',   x: -1, y:  0}
		 | {key: 'right',  x:  1, y:  0};

	constructor(...args) {
		super(...args);
		this.side = args.side;
	}

}

import assert from 'power-assert';
import $      from '../libs/jquery.js';
import {isBoolean as _isBoolean} from 'lodash';
import {entries, values, isUndefined} from 'lodash-bound';

import {ID_MATRIX, SVGMatrix, setCTM, Point2D} from '../util/svg.js';
import {property, flag} from 'utilities';


import {SvgTransformable} from './SvgTransformable.js';

/**
 * Representation of a Box corner.
 */
export class BoxCorner extends SvgTransformable {

	static RADIUS = 15;

	@flag({ initial: false }) rounded;

	preCreate(options) {
		super.preCreate(options);

		/* set properties if given */
		if (!options.rounded::isUndefined()) { this.rounded = options.rounded }
	}

	create(options = {}) {
		super.create(options);

		const handlePath = $.svg(`<path>`).css({
			strokeWidth: 'inherit',
		}).appendTo(this.svg.handles);

		const inkPath = $.svg(`<path>`).css({
			fill:             'transparent',
			stroke:           'inherit',
			strokeWidth:      'inherit',
			strokeDasharray:  'inherit',
			strokeDashoffset: 'inherit'
		}).appendTo(this.svg.ink);

		const overlayFillPath = $.svg(`<path>`).css({
			fill:            'inherit',
			stroke:          'black',
			strokeDasharray: 'none'
		}).appendTo(this.svg.overlay);

		const overlayStrokePath = $.svg(`<path>`).css({
			fill:             'transparent',
			stroke:           'inherit',
			strokeWidth:      'inherit',
			strokeDasharray:  'inherit',
			strokeDashoffset: 'inherit'
		}).appendTo(this.svg.overlay);

		/* react to resizing and roundedness-toggling */
		const s = BoxCorner.RADIUS;
		const rightAngle  = `L 0 0 L`;
		const arcAngle    = `A ${s} ${s}, 0, 0, 0,`;
		const bl          = `0 ${s}`;
		const tr          = `${s} 0`;
		const outerCorner = r => `M ${tr} ${r ? arcAngle : rightAngle} ${bl}`;
		const innerCorner = `M ${tr} L ${bl}`;
		this.p('rounded').subscribe((r) => {
			handlePath       .attr({ d: outerCorner(0)       });
			inkPath          .attr({ d: outerCorner(r)       });
			overlayFillPath  .attr({ d: outerCorner(r) + 'Z' });
			overlayStrokePath.attr({ d: innerCorner          });
		});

	}

	postCreate(options = {}) {
		super.postCreate(options);

		setTimeout(() => {
			/* set the stroke-width of part of the overlay based on part of the ink */
			const source = this.svg.ink    .children('[style*="stroke: inherit"]');
			const target = this.svg.overlay.children('[style*="stroke: black"]');
			target.css({ strokeWidth: getComputedStyle(source[0])['stroke-width'] });
		});
	}

}

import {SvgArtefact} from './SvgArtefact.js';
import {SvgTransformable} from './SvgTransformable';
import {property} from 'utilities';
import {ID_MATRIX, setCTM} from '../util/svg';

/**
 * Representation of an svg canvas used to house boxer artefacts.
 */
export class Canvas extends SvgArtefact {

	@property({ initial: ID_MATRIX, isValid: v => v instanceof SVGMatrix }) transformation;
	@property({ initial: ID_MATRIX, isValid: v => v instanceof SVGMatrix }) globalTransformation;

	preCreate(options = {}) {
		super.preCreate(options);

		/* set transformation if given */
		if (options.transformation) { this.transformation = options.transformation }

		/* apply canvas transformation (zooming, panning, etc.) */
		this.p('transformation').subscribe( this.svg.children::setCTM );

		/* keep track of the global transformation */
		this.p('transformation').subscribe(this.p('globalTransformation'));
	}

	postCreate(options = {}) {

		/* register canvas handlers */
		this.registerHandlers({
			dropzone:  {
				artefact: this,
				accepts: () => true,
				after: ({artefact}) => {
					artefact.parent = this;
				}
			},
			drawzone: {
				artefact: this,
				accepts({ artefact }) {
					return artefact instanceof SvgTransformable;
				}
			},
			pannable: {
				artefact: this
			}
		});
		super.postCreate(options);
	}

	registerHandlers(handlers: Object = {}) {
		// Canvas is special: its handler element is this.svg.main
		super.registerHandlers(handlers);
		this.svg.main.data('boxer-handlers', this.handlers);
	}

}

import assert from 'power-assert';
import $      from '../libs/jquery.js';
import {Observable} from '../libs/expect-rxjs.js';

import {isEmpty, isUndefined} from 'lodash-bound';

import {Point2D} from '../util/svg.js';
import {property} from 'utilities';

import {LineSegment} from './LineSegment.js';
import {Glyph}       from './Glyph.js';
import {moveToFront} from '../util/svg';
import {smartMerge} from '../Coach';


/**
 * Representation of an interactive edge in svg space, spanning between two glyphs.
 */
export class Edge extends LineSegment {

	@property({ isValid: v => v instanceof Glyph }) glyph1;
	@property({ isValid: v => v instanceof Glyph }) glyph2;

	preCreate(options = {}) {
		super.preCreate({
			// lengthen1: -Glyph.RADIUS,
			// lengthen2: -Glyph.RADIUS,
			...options
		});

		/* initializing glyphs */
		for (let g of [1, 2]) {
			// assert(options[`glyph${g}`]);
			this[`glyph${g}`] = options[`glyph${g}`];
		}

		/* re-parenting this Edge when either of the glyphs re-parent */
		this.p(['glyph1', 'glyph2']).filter(([g1, g2]) => g1 && g2).switchMap(([g1, g2]) => Observable.combineLatest(
			g1.p('depth').filter(d => !d::isUndefined() && !g1.deleted),
			g2.p('depth').filter(d => !d::isUndefined() && !g2.deleted)
		).map(() => g1.closestCommonAncestorWith(g2))).subscribe( this.p('parent') );

		/* moving the line segment when the glyphs move */
		for (let g of [1, 2]) {
			this.p([`glyph${g}.globalTransformation`], ['root'])
			    .map(([gt, root]) => Point2D.fromMatrixTranslation(gt, root.svg.main))
			    .subscribe( this.p(`point${g}`) );
		}

		/* lengthen/shorten the line segment when the glyph radii change */
		for (let g of [1, 2]) {
			this.p(`glyph${g}.radius`)
			    .map(r => -r)
			    .subscribe( this.p(`lengthen${g}`) );
		}

		/* propagate moveToFront  */
		const thisArtefact = this;
		function direction(d)    { return this.filter(({direction}) => (direction === d))   }
		function doNotTurnBack() { return this.filter(info => info.source !== thisArtefact) }
		for (let g of [1, 2]) {
			this.e(`glyph${g}.moveToFront`)
				::doNotTurnBack()
				.subscribe((info) => { this.e('moveToFront').next({ ...info, direction: 'in' }) });
		}
		// As a special exception for Edge, inward moveToFront also brings svg to front.
		// Outward moveToFront already does this (see SvgArtefact.js)
		this.e('moveToFront')
			::direction('in')
			.subscribe(this.svg.main::moveToFront);
	}

	create(options = {}) {
		super.create(options);


		const inkLineOutline = $.svg('<line>').css({
			stroke:           'black',
			strokeWidth:      '4'
		}).prependTo(this.svg.ink);

		this.p(['parent', 'point1', 'point2', 'lengthen1', 'lengthen2'])
		    .filter(([parent, p1, p2]) => parent && p1 && p2)
		    .subscribe(([parent, p1, p2, l1, l2]) => {
				p1 = p1.in(parent.svg.children);
				p2 = p2.in(parent.svg.children);
				$(inkLineOutline).attr({
					...p1.withDistanceTo(-l2, p2).obj('x1', 'y1'),
					...p2.withDistanceTo(-l1, p1).obj('x2', 'y2')
				});
			});

	}

	postCreate(options = {}) {

		/* set standard handlers */
		this.registerHandlers({
			highlightable: {
				artefact: this,
				effect: { elements: this.svg.overlay }
			},
			deletable: {
				artefact: this
			}
		});

		/* delete this when either glyph is deleted */
		Observable.merge(
			this.p('glyph1.deleted').filter(d=>!!d),
			this.p('glyph2.deleted').filter(d=>!!d)
		).take(1).subscribe( this.p('deleted') );

		/* set glyph handlers */
		const handlers = {
			movable: {
				before: () => { this.handlesActive = false },
				after:  () => { this.handlesActive = true  }
			}
		};
		this.p('glyph1').filter(g=>!!g).subscribe((glyph1) => { glyph1.registerHandlers(handlers) });
		this.p('glyph2').filter(g=>!!g).subscribe((glyph2) => { glyph2.registerHandlers(handlers) });
		// this.glyph2.registerHandlers(handlers);

		/***/
		super.postCreate(options);
	}

}

