}

import RxCSS from 'rxcss';
import {sineWave, animationFrames} from '../util/misc';
import {sum} from 'lodash-bound';
import {GlobalBehaviorTool} from './GlobalBehaviorTool';

const {floor, PI, min} = Math;


/**
 * A tool that can create an animated color-shifting highlighting border
 * around some artifact based on some observable conditions.
 * Those specific conditions and the nature of the visual border
 * are defined inside the tools that want to use highlighting
 * to signal their state, using `coach.highlightTool.register()`.
 */
export class HighlightTool extends GlobalBehaviorTool {

	/* 'selectable' related highlighting */
	HIGHLIGHT_DEFAULT = (() => {
		const redWave   = sineWave({ amplitude: 40, period: 10000               },
		                           { amplitude: 30, period:   800, phase: 50*PI });
		const greenWave = sineWave({ amplitude: 30, period:  3000               });
		const blueWave  = sineWave({ amplitude: 30, period:  6000, phase: 1500  });
		return {
			colorCycle: () => {
				const t = Date.now();
				return `rgb(
					${floor(min( 255, 160+redWave  (t) ))},
					${floor(min( 255, 225+greenWave(t) ))},
					${floor(min( 255, 225+blueWave (t) ))}
				)`;
			},
			dashCycle: () => {
				const t = Date.now();
				return floor(t % 1000 / 1000 * 45);
			},
			dashArray: [10, 5],
			dashSpeed: 3
		};
	})();


	deactivateBehavior(previous) {
		/* set value */
		this.currentValue = null;
		/* set styling */
		const handler = previous.artefact.handlers.highlightable;
		if (!handler) { return }
		const css = { opacity: 0 };
		if (handler.effect.elements) { handler.effect.elements.css(css)                    }
		if (handler.effect.selector) { artefact.setCSS({ [handler.effect.selector]: css }) }
	}

	activateBehavior(current) {
		/* set value */
		this.currentValue = current;
		/* set styling */
		const handler = current.artefact.handlers.highlightable;
		if (!handler) { return }
		const css = {
			strokeWidth:      4,
			opacity:          1,
			strokeDasharray:  current.dashArray.join(','),
			stroke:           'var(--boxer-highlight-color)',
			fill:             'var(--boxer-highlight-color)',
			strokeDashoffset: 'var(--boxer-highlight-dash-offset)'
		};
		if (handler.effect.elements) { handler.effect.elements.css(css)                    }
		if (handler.effect.selector) { artefact.setCSS({ [handler.effect.selector]: css }) }
	}


	init({coach}) {
		super.init({ coach });

		/* set initial behavior to 'no effect' = null */
		this.currentValue = null;

		/* one-time setup of RxCSS */
		RxCSS({
			'boxer-highlight-color': animationFrames
				.filter(() => this.currentValue)
				.map   (() => this.currentValue.colorCycle()),
			'boxer-highlight-dash-offset': animationFrames
				.filter(() => this.currentValue)
				.map   (() => {
					const t = Date.now();
					const speed = this.currentValue.dashSpeed * this.currentValue.dashArray::sum();
					return floor(t % 1000 / 1000 * speed);
				})
		});//, this.coach.root.svg.main::plainDOM());
	}
}

import $ from '../libs/jquery.js';
import {GlobalBehaviorTool} from './GlobalBehaviorTool';

/**
 * A tool that can change the mouse cursor based on some observable conditions.
 * Those specific conditions and the specific mouse-cursor to use
 * are defined inside the tools that want to use mouse cursors
 * to signal their state; using `coach.mouseCursorTool.register()`.
 */
export class MouseCursorTool extends GlobalBehaviorTool {

	deactivateBehavior() {
		$(this.coach.root.svg.main).css({ cursor: '' });
	}

	activateBehavior(cursor) {
		$(this.coach.root.svg.main).css({ cursor });
	}

}

import {pick} from 'lodash-bound';

import Tool from './Tool';
import {which} from 'utilities';
import {emitWhenComplete} from '../util/misc.js';

import Machine from '../util/Machine';
import KeyCode from 'keycode-js';
const {KEY_ESCAPE} = KeyCode;


/**
 * An abstract tool used to create other tools that use mouse input.
 * It supplies a state machine with meaningful mouse-based states.
 * (Also happens to signal when ESCAPE is pressed. Not mouse based,
 *  but it made sense in context.)
 */
export class MouseTool extends Tool {

	static DRAG_THRESHOLD = 4;

	init({ coach, events = [] }) {
		super.init({ coach, events: [...events, 'mousedown'] });

		const mousemove = this.windowE('mousemove');
		const mouseup   = this.windowE('mouseup');
		const keydown   = this.windowE('keydown');

		this.mouseMachine = new Machine('Mouse', { state: 'IDLE' });

		let selectedArtefact = null; // TODO: Why is this needed? .withLatestFrom() isn't working.
		coach.p('selectedArtefact').subscribe((a) => { selectedArtefact = a });

		this.mouseMachine.extend(({ enterState }) => ({
			'IDLE': () => [
		        this.e('mousedown')
		            .filter(() => this.active)
		            .map(e => [e, selectedArtefact])
		            .filter(([e, a]) => !!a)
		            .map(([e, artefact]) => ({
			            point:    e.point,
				        artefact: artefact,
			            ...e::pick('shiftKey', 'ctrlKey', 'altKey')
			        }))
			        ::enterState('THRESHOLD'),
		        keydown::which(KEY_ESCAPE)::enterState('ESCAPING')
			],
			'THRESHOLD': (args) => [
				mousemove
					.filter(() => this.active)
					.take(MouseTool.DRAG_THRESHOLD)
					.ignoreElements()
					::emitWhenComplete(args)
					::enterState('DRAGGING'),
			    mouseup
				    ::enterState('CLICKING'),
			    keydown
					::which(KEY_ESCAPE)
			        ::enterState('ESCAPING')
			],
			'DRAGGING': () => [
	            mouseup::enterState('DROPPING'),
                keydown::which(KEY_ESCAPE)::enterState('ESCAPING')
			],
			'CLICKING': () => { enterState('IDLE') },
			'DROPPING': () => { enterState('IDLE') },
			'ESCAPING': () => { enterState('IDLE') }
		}));
	}

}

import $ from '../libs/jquery.js';
import {isFunction} from 'lodash-bound';
import CSSPrefix from 'cssprefix/src/cssprefix';

import {Observable} from '../libs/expect-rxjs.js';

import {handleBoxer} from '../Coach.js';
import {withoutMod, match} from 'utilities';

import {snap45} from "../util/svg";
import {MouseTool} from './MouseTool';
import {callIfFunction} from '../util/misc';
import Machine from '../util/Machine';


/**
 * A tool for moving artefacts around, and in and out of other artefacts,
 * using drag-and-drop.
 */
export class MoveTool extends MouseTool {

	init({ coach }) {
		super.init({ coach });

		/* relevant mouse events */
		const mousemove = this.windowE('mousemove');
		const threshold = this.mouseMachine.THRESHOLD
			.filter(withoutMod('shift', 'ctrl', 'alt'))
			::handleBoxer('movable');
		const dragging = this.mouseMachine.DRAGGING
			::handleBoxer('movable');
		const dropping = this.mouseMachine.DROPPING;
		const escaping = this.mouseMachine.ESCAPING;

		/* main state machine of this tool */
		const localMachine = new Machine('MoveTool', { state: 'IDLE' });
		localMachine.extend(({ enterState, subscribeDuringState }) => ({
			'IDLE': () => {

				// console.log('(((IDLE)))');

				threshold::enterState('THRESHOLD');
				coach.selectTool.reacquire();
			},
			'THRESHOLD': () => {

				// console.log('(((THRESHOLD)))');

				dragging::enterState('DRAGGING');
				this.mouseMachine.IDLE::enterState('IDLE');
			},
			'DRAGGING': (args) => {

				// console.log('(((DRAGGING)))');

				const {point, artefact, before, after, cancel, referencePoint = point} = args;

				/* drag initialization */
				artefact.handlesActive = false;
				coach.selectTool.reacquire();
				artefact.moveToFront();
				if (before::isFunction()) { before(args) }

				/* record start transformation */
				const transformationStart = artefact.transformation;

				/* move while dragging */
				mousemove::subscribeDuringState((moveEvent) => {
					let mouseVector = moveEvent.point.in(artefact.svg.children);
					if (referencePoint && moveEvent.ctrlKey) {
						mouseVector = mouseVector::snap45(referencePoint);
					}
					let translationDiff = mouseVector.minus(point);
					artefact.transformation = transformationStart
						.translate(...translationDiff.xy);
				});

				/* cancel or stop dragging */
				// TODO: don't use exceptions for cancellation; separate it from the concept of dropping
				Observable.merge(
					escaping
						.concatMap(Observable.throw()),
					dropping
						.map(({point, target}) => ({ dropzone: $(target).data('boxer-handlers').dropzone, point }))
						.do(({dropzone: {after}}) => { after::callIfFunction(args) })
				).catch((error, caught) => {
					/* cancel dragging */
					artefact.transformation = transformationStart;
					cancel::callIfFunction(args);
					return Observable.of({});
                }).do(({point}) => {
					/* stop dragging */
					artefact.handlesActive = true;
					artefact.moveToFront();
					coach.selectTool.reacquire(point);
					after::callIfFunction(args);
				})::enterState('IDLE');

			}
		}));


		/* mutual exclusion between this machine and other machines, coordinated by coach.stateMachine */
		localMachine.extend(() => ({ 'OTHER_TOOL': ()=>{} }));
		coach.stateMachine.extend(() => ({ 'IDLE': ()=>{}, 'BUSY': ()=>{} }));
		localMachine.link('IDLE',       coach.stateMachine.BUSY.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'OTHER_TOOL');
		localMachine.link('OTHER_TOOL', coach.stateMachine.IDLE.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'IDLE');
		coach.stateMachine.link('IDLE', localMachine.DRAGGING.map(() => ({ tool: this })), 'BUSY');
		coach.stateMachine.link('BUSY', localMachine.IDLE    .map(() => ({ tool: this })), 'IDLE');

		/* prep for highlighting and mouse cursors */
		const handlerArtefactOrNull = (key) => (a) => (a && a.handlers[key]) ? a.handlers[key].artefact : null;
		const movableArtefact  = coach.p('selectedArtefact').map(handlerArtefactOrNull('movable'));
		const dropzoneArtefact = coach.p('selectedArtefact').map(handlerArtefactOrNull('dropzone'));

		/* highlighting */
		coach.highlightTool.register(this, localMachine.p('state').switchMap(state => match(state)({
			'IDLE':       movableArtefact,//.do((ma) => { console.log('---', ma && ma.constructor.name) }),
			'THRESHOLD':  movableArtefact,
			'DRAGGING':   dropzoneArtefact.startWith(null),
			'OTHER_TOOL': Observable.of(null)
		})).map(artefact => artefact && {
			...coach.highlightTool.HIGHLIGHT_DEFAULT,
			artefact
		}));

		/* mouse cursors */
		const grabCursor     = CSSPrefix.getValue('cursor', 'grab'    );
		const grabbingCursor = CSSPrefix.getValue('cursor', 'grabbing');
		coach.mouseCursorTool.register(this, localMachine.p('state').startWith(null).pairwise().switchMap(([prev, next]) => match(next)({
			'IDLE':       movableArtefact.map(ma => ma && grabCursor).startWith(prev && grabCursor),
			'THRESHOLD':  Observable.of(grabbingCursor),
			'DRAGGING':   Observable.of(grabbingCursor),
			'OTHER_TOOL': Observable.of(null)
		})));

	}



}

import {isFunction} from 'lodash-bound';
import {Observable} from '../libs/expect-rxjs.js';
import CSSPrefix from 'cssprefix/src/cssprefix';

import {match} from 'utilities';

import {handleBoxer} from '../Coach.js';
import {MouseTool} from './MouseTool';
import Machine from '../util/Machine';

import KeyCode from 'keycode-js';
import {callIfFunction} from '../util/misc';

const {KEY_ESCAPE} = KeyCode;

const {max, sign, abs} = Math;

const DRAWING_BOX   = 'DRAWING_BOX';
const DRAWING_GLYPH = 'DRAWING_GLYPH';
const DRAWING_EDGE  = 'DRAWING_EDGE';
const DRAWING_EDGES = 'DRAWING_EDGES';
const MODES = [
	DRAWING_BOX,
	DRAWING_GLYPH,
	DRAWING_EDGE,
	DRAWING_EDGES
];


/**
 * A tool for panning the main canvas using drag-and-drop.
 */
export class PanTool extends MouseTool {

	init({coach}) {
		super.init({ coach });

		const mousemove = this.windowE('mousemove');
		const idle      = this.mouseMachine.IDLE;
		const threshold = this.mouseMachine.THRESHOLD
			::handleBoxer('pannable');
		const dragging = this.mouseMachine.DRAGGING
			::handleBoxer('pannable');
		const dropping = this.mouseMachine.DROPPING;

		/* local state machine */
		const localMachine = new Machine(this.constructor.name, { state: 'IDLE' });
		localMachine.extend(({ enterState, subscribeDuringState }) => ({
			'IDLE': () => {
				threshold::enterState('THRESHOLD');
			},
			'THRESHOLD': () => {
				dragging::enterState('PANNING');
				idle    ::enterState('IDLE');
			},
			'PANNING': (args) => {
				const {before, after, point, artefact} = args;

				/* pan initialization */
				if (before::isFunction()) { before(args) }

				/* record start dimensions and mouse position */
				const start = {
					transformation: artefact.transformation,
					mouse:          point
				};

				/* resize while dragging */
				mousemove
					.map(event => event.point.in(artefact.svg.children).minus(start.mouse))
					.map(({x: xDiff, y: yDiff}) => start.transformation.translate(xDiff, yDiff))
					::subscribeDuringState((m) => { artefact.transformation = m });

				/* cancel or stop dragging */
				dropping.do(({point}) => {
					/* stop drawing */
					// coach.selectTool.reacquire(point);
					coach.selectTool.reacquire();
					after::callIfFunction(); // TODO: pass args?
				})::enterState('IDLE');

			}
		}));

		/* mutual exclusion between this machine and other machines, coordinated by coach.stateMachine */
		localMachine.extend(() => ({ 'OTHER_TOOL': ()=>{} }));
		coach.stateMachine.extend(() => ({ 'IDLE': ()=>{}, 'BUSY': ()=>{} }));
		localMachine.link('IDLE',       coach.stateMachine.BUSY.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'OTHER_TOOL');
		localMachine.link('OTHER_TOOL', coach.stateMachine.IDLE.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'IDLE');
		coach.stateMachine.link('IDLE', localMachine.PANNING.map(() => ({ tool: this })), 'BUSY');
		coach.stateMachine.link('BUSY', localMachine.IDLE   .map(() => ({ tool: this })), 'IDLE');

		/* prep for mouse cursors */
		const handlerArtefactOrNull = (key) => (a) => (a && a.handlers[key]) ? a.handlers[key].artefact : null;
		const canvasArtefact = coach.p('selectedArtefact').map(handlerArtefactOrNull('pannable'));

		/* mouse cursors */
		const grabCursor     = CSSPrefix.getValue('cursor', 'grab'    );
		const grabbingCursor = CSSPrefix.getValue('cursor', 'grabbing');
		coach.mouseCursorTool.register(this, localMachine.p('state').startWith(null).pairwise().switchMap(([prev, next]) => match(next)({
			'IDLE':       canvasArtefact.map(ma => ma && grabCursor).startWith(prev && grabCursor),
			'THRESHOLD':  Observable.of(grabbingCursor),
			'PANNING':    Observable.of(grabbingCursor),
			'OTHER_TOOL': Observable.of(null)
		})));
	}
}

import $ from '../libs/jquery.js';
import {assign, isFunction} from 'lodash-bound';
import {Observable} from '../libs/expect-rxjs.js';

import {withoutMod, match} from 'utilities';

import {M21, M22} from "../util/svg";

import {handleBoxer} from '../Coach.js';
import {MouseTool} from './MouseTool';
import {plainDOM} from '../libs/jquery';
import Machine from '../util/Machine';
import {callIfFunction} from '../util/misc';

const {min, max, floor} = Math;


/**
 * A tool for resizing boxes by dragging its borders or corners with the mouse.
 */
export class ResizeTool extends MouseTool {

	init({coach}) {
		super.init({ coach });

		/* extend state machine */
		const mousemove = this.windowE('mousemove');
		const threshold = this.mouseMachine.THRESHOLD
			.filter(() => this.active)
			.filter(withoutMod('shift', 'ctrl', 'alt'))
			::handleBoxer('resizable');
		const dragging = this.mouseMachine.DRAGGING
			.filter(() => this.active)
			::handleBoxer('resizable');
		const dropping = this.mouseMachine.DROPPING;
		const escaping = this.mouseMachine.ESCAPING;

		/* determining proper resizing cursor */
		const borderCursor = (handler) => {
			if (!handler) { return null }

			let m = handler.artefact.svg.main::plainDOM().getScreenCTM();
			let angle = Math.atan2(m[M21], m[M22]) * 180 / Math.PI;

			let {x, y} = handler.directions;
			x = (x === -1) ? '-' : (x === 1) ? '+' : '0';
			y = (y === -1) ? '-' : (y === 1) ? '+' : '0';
			switch (x+' '+y) {
				case '0 -': { angle +=   0 } break;
				case '+ -': { angle +=  45 } break;
				case '+ 0': { angle +=  90 } break;
				case '+ +': { angle += 135 } break;
				case '0 +': { angle += 180 } break;
				case '- +': { angle += 225 } break;
				case '- 0': { angle += 270 } break;
				case '- -': { angle += 315 } break;
			}
			angle = (angle + 360) % 360;
			return [
				'ns-resize',   // 0,   0:  |
				'nesw-resize', // 1,  45:  /
				'ew-resize',   // 2,  90:  -
				'nwse-resize'  // 3, 135:  \
			][floor((angle + 180/8) % 180 / (180/4)) % 4];
		};

		/* main state machine of this tool */
		const localMachine = new Machine('ResizeTool', { state: 'IDLE' });
		localMachine.extend(({ enterState, subscribeDuringState }) => ({
			'IDLE': () => {
				threshold::enterState('THRESHOLD');
				coach.selectTool.reacquire();
			},
			'THRESHOLD': () => {
				dragging::enterState('DRAGGING');
				this.mouseMachine.IDLE::enterState('IDLE');
			},
			'DRAGGING': (args) => {
				const {point, artefact, before, after, cancel, directions} = args;

				/* drag initialization */
				artefact.handlesActive = false;
				coach.selectTool.reacquire();
				artefact.moveToFront();
				if (before::isFunction()) { before(args) }

				/* record start dimensions and mouse position */
				const start = {
					transformation: artefact.transformation,
					width:          artefact.width,
					height:         artefact.height,
					mouse:          point
				};

				/* resize while dragging */
				mousemove
					.map(event => event.point.in(artefact.svg.children).minus(start.mouse))
					::subscribeDuringState(({x: xDiff, y: yDiff}) => {
						xDiff = directions.x * max(directions.x * xDiff, artefact.minWidth  - start.width );
						yDiff = directions.y * max(directions.y * yDiff, artefact.minHeight - start.height);
						artefact::assign({
							transformation: start.transformation.translate(xDiff/2, yDiff/2),
							width:          start.width  + directions.x * xDiff,
							height:         start.height + directions.y * yDiff
						});
					});

				/* cancel or stop dragging */
				Observable.merge(
					escaping.concatMap(Observable.throw()),
					dropping.map(({point, target}) => ({
						dropzone: $(target).data('boxer-handlers').dropzone,
						point
					})).do(({dropzone}) => {
						// artefact.parent = dropzone.artefact; // TODO: Maybe we'll need this to snap borders
						dropzone.after::callIfFunction(args);
					})
				).catch((error, caught) => {
					/* cancel dragging */
					artefact.transformation = start.transformation;
					artefact.width  = start.width;
					artefact.height = start.height;
					cancel::callIfFunction(args);
					return Observable.of({});
                }).map(({point}) => {
					/* stop dragging */
					artefact.handlesActive = true;
					artefact.moveToFront();
					coach.selectTool.reacquire(point);
					after::callIfFunction(); // TODO: pass args?
					return { ...args, point };
				})::enterState('IDLE');

			}
		}));

		/* mutual exclusion between this machine and other machines, coordinated by coach.stateMachine */
		localMachine.extend(() => ({ 'OTHER_TOOL': ()=>{} }));
		coach.stateMachine.extend(() => ({ 'IDLE': ()=>{}, 'BUSY': ()=>{} }));
		localMachine.link('IDLE',       coach.stateMachine.BUSY.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'OTHER_TOOL');
		localMachine.link('OTHER_TOOL', coach.stateMachine.IDLE.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'IDLE');
		coach.stateMachine.link('IDLE', localMachine.DRAGGING.map(() => ({ tool: this })), 'BUSY');
		coach.stateMachine.link('BUSY', localMachine.IDLE    .map(() => ({ tool: this })), 'IDLE');

		/* prep for highlighting and mouse cursors */
		const handlerOrNull = (key) => (a) => (a && a.handlers[key] && a.handlers['highlightable']) ? a.handlers[key] : null;
		const resizableArtefact = coach.p('selectedArtefact').map((originalArtefact) => {
			let handler = handlerOrNull('resizable')(originalArtefact);
			if (!handler) { return null }
			return { originalArtefact, ...handler };
		});

		/* highlighting */
		coach.highlightTool.register(this, localMachine.p(['state', 'data']).switchMap(([state, data]) => match(state)({
			'IDLE':       resizableArtefact,
			'THRESHOLD':  Observable.of(data),
			'DRAGGING':   Observable.of(data),
			'OTHER_TOOL': Observable.of(null)
		})).map((handler) => handler && {
			...coach.highlightTool.HIGHLIGHT_DEFAULT,
			artefact: handler.originalArtefact
		}));

		/* mouse cursors */
		coach.mouseCursorTool.register(this, localMachine.p(['state', 'data']).switchMap(([state, data]) => match(state)({
			'IDLE':       resizableArtefact.map(borderCursor),
			'THRESHOLD':  Observable.of(borderCursor(data)),
			'DRAGGING':   Observable.of(borderCursor(data)),
			'OTHER_TOOL': Observable.of(null)
		})));

	}
}

import {isFunction} from 'lodash-bound';

import {TweenLite} from 'gsap/TweenLite';

import {handleBoxer} from '../Coach.js';
import {withoutMod, withMod, match} from 'utilities';

import {M21, M22, Point2D} from "../util/svg";
import {MouseTool} from './MouseTool';
import {plainDOM} from '../libs/jquery';
import {Observable} from '../libs/expect-rxjs.js';
import {callIfFunction} from '../util/misc';
import Machine from '../util/Machine';
import CSSPrefix from 'cssprefix/src/cssprefix';

const {floor, abs, round, atan2, PI} = Math;


/**
 * A tool to rotate boxes and their content by drag-and-drop while SHIFT is pressed.
 */
export class RotateTool extends MouseTool {

	static SNAP_ANGLE = 45;

	init({ coach }) {
		super.init({ coach });

		/* relevant mouse events */
		const mousemove = this.windowE('mousemove');
		const threshold = this.mouseMachine.THRESHOLD
			.filter(() => this.active)
			.filter(withMod('shift')).filter(withoutMod('ctrl'))
			::handleBoxer('rotatable');
		const dragging = this.mouseMachine.DRAGGING
			.filter(() => this.active)
			::handleBoxer('rotatable');
		const dropping = this.mouseMachine.DROPPING;
		const escaping = this.mouseMachine.ESCAPING;

		/* main state machine of this tool */
		const localMachine = new Machine(this.constructor.name, { state: 'IDLE' });
		localMachine.extend(({ enterState, subscribeDuringState }) => ({
			'IDLE': () => {
				threshold::enterState('THRESHOLD');
				coach.selectTool.reacquire();
			},
			'THRESHOLD': () => {
				dragging::enterState('DRAGGING');
				this.mouseMachine.IDLE::enterState('IDLE');
			},
			'DRAGGING': (args) => {
				const {point, artefact, before, after, cancel, referencePoint = point} = args;

				/* drag initialization */
				artefact.handlesActive = false;
				coach.selectTool.reacquire();
				artefact.moveToFront();
				if (before::isFunction()) { before(args) }

				/* pre-processing */
				const start = {};
				start.transformation           = artefact.transformation;
				start.angle                    = atan2(start.transformation[M21], start.transformation[M22]) * 180 / PI;
				start.nonRotatedTransformation = start.transformation.rotate(-start.angle); // TODO: faster way to get non-rotated version of matrix (resetting M21 and M22?)
				start.center                   = Point2D.fromMatrixTranslation(start.nonRotatedTransformation, artefact.svg.main.parent()::plainDOM());
				start.mouseAngle               = referencePoint.minus(start.center).angle() - start.angle;

				/* rotate while dragging */
				let tracking = {
					snapping: false,
					angle:    start.angle
				};
				mousemove.map((moveEvent) => {
					let angle = moveEvent.point.minus(start.center).angle();
					angle -= start.mouseAngle;
					tracking.snapping = moveEvent.altKey;
					if (tracking.snapping) {
						angle = round(angle / RotateTool.SNAP_ANGLE) * RotateTool.SNAP_ANGLE;
					}
					return (angle + 360) % 360;
				}).distinctUntilChanged().switchMap((angle) => {
					if (tracking.snapping) {
						let diff = angle - tracking.angle;
						while (diff < -180) { angle += 360; diff += 360; }
						while (diff > +180) { angle -= 360; diff -= 360; }
						return Observable.create((obs) => TweenLite.to(
							tracking,
							abs(diff) / 45 * 0.2,
							{
								angle,
								ease: TweenLite.Power3.easeOut,
								onUpdate: ::obs.next,
								onComplete: () => { tracking.angle = (tracking.angle % 360 + 360) % 360 }
							}
						)).share().map(()=>tracking.angle);
					} else {
						return Observable.of(angle);
					}
				})::subscribeDuringState((angle) => {
					artefact.transformation = start.nonRotatedTransformation.rotate(angle);
				});

				/* cancel or stop dragging */
				Observable.merge(
					escaping.concatMap(Observable.throw()),
					dropping
				).catch((error, caught) => {
					/* cancel dragging */
					artefact.transformation = start.transformation;
					cancel::callIfFunction(args);
					return Observable.of({});
                }).do(({point}) => {
					/* stop dragging */
					artefact.handlesActive = true;
					coach.selectTool.reacquire(point);
					after::callIfFunction(); // TODO: pass args?
				})::enterState('IDLE');

			}
		}));

		/* mutual exclusion between this machine and other machines, coordinated by coach.stateMachine */
		localMachine.extend(() => ({ 'OTHER_TOOL': ()=>{} }));
		coach.stateMachine.extend(() => ({ 'IDLE': ()=>{}, 'BUSY': ()=>{} }));
		localMachine.link('IDLE',       coach.stateMachine.BUSY.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'OTHER_TOOL');
		localMachine.link('OTHER_TOOL', coach.stateMachine.IDLE.filter(({tool}) => tool !== this).map(()=>localMachine.data), 'IDLE');
		coach.stateMachine.link('IDLE', localMachine.DRAGGING.map(() => ({ tool: this })), 'BUSY');
		coach.stateMachine.link('BUSY', localMachine.IDLE    .map(() => ({ tool: this })), 'IDLE');

		/* prep for highlighting and mouse cursors */
		const handlerOrNull = (key) => (a) => (a && a.handlers[key] && a.handlers['highlightable']) ? a.handlers[key].artefact : null;
		const rotatableArtefact = coach.p('selectedArtefact').map((originalArtefact) => {
			let handler = handlerOrNull('rotatable')(originalArtefact);
			if (!handler) { return null }
			return { originalArtefact, ...handler };
		});

		/* highlighting */
		coach.highlightTool.register(this, localMachine.p(['state', 'data']).switchMap(([state, data]) => match(state)({
			'IDLE':       rotatableArtefact,
			'THRESHOLD':  Observable.of(data),
			'DRAGGING':   Observable.of(data),
			'OTHER_TOOL': Observable.of(null)
		})).map(handler => handler && {
			...coach.highlightTool.HIGHLIGHT_DEFAULT,
			artefact: handler.originalArtefact
		}));

		/* mouse cursors */
		const grabCursor     = CSSPrefix.getValue('cursor', 'grab'    );
		const grabbingCursor = CSSPrefix.getValue('cursor', 'grabbing');
		coach.mouseCursorTool.register(this, localMachine.p('state').startWith(null).pairwise().switchMap(([prev, next]) => match(next)({
			'IDLE':       rotatableArtefact.map(ma => ma && grabCursor).startWith(prev && grabCursor),
			'THRESHOLD':  Observable.of(grabbingCursor),
			'DRAGGING':   Observable.of(grabbingCursor),
			'OTHER_TOOL': Observable.of(null)
		})));

	}



}

import $ from '../libs/jquery.js';
import Tool from './Tool';
import {handleBoxer} from '../Coach.js';
import {Observable} from '../libs/expect-rxjs.js';
import {withMod} from 'utilities';
import {elementController} from '../Coach';
import {Point2D} from '../util/svg';


/**
 * A tool that keeps track of which artefact is 'selected'. That is,
 * which artefact has the mouse currently pointing at it, but also
 * taking other factors into account. For example, the selection
 * can be shifted along the artefact nesting hierarchy by using the
 * scroll-wheel while ALT is pressed.
 */
export class SelectTool extends Tool {

	init({coach}) {
		super.init({ coach, events: ['mouseover', 'mouseout', 'mouseenter', 'mouseleave'] });

		this.reacquire = ()=>{};
		const reacquires = Observable.create((observer) => {
			this.reacquire = (point) => {
				let element;
				if (point) {
					point = point.in(coach.root.svg.children);
					let {left, top} = coach.root.svg.main.offset();
					element = document.elementFromPoint(
						left + point.x,
						top  + point.y
					);
				} else {
					let hover = $(':hover');
					element = hover[hover.length-1];
				}
				if (element instanceof SVGElement) {
					observer.next({
						artefact: elementController(element),
						point: new Point2D({ x: 0, y: 0, coordinateSystem: element })
					});
				}
			};
			return () => {
				this.reacquire = ()=>{};
			};
		}).share();

		coach.setSelectedArtefact = ()=>{};
		const apiAcquires = Observable.create((observer) => {
			coach.setSelectedArtefact = (artefact) => {
				if (!artefact) {
					observer.next(null);
				} else {
					const element = artefact.svg.handles.find('*')[0];
					if (element) {
						observer.next({
							artefact: elementController(element),
							point: new Point2D({ x: 0, y: 0, coordinateSystem: element })
						});
					}
				}
			};
			return () => {
				coach.setSelectedArtefact = ()=>{};
			};
		}).share();

		const mouseEnter = this.e('mouseover')
			.merge(reacquires)
			.merge(apiAcquires)
			::handleBoxer('*') // TODO: <-- selectable?
			.map(handler => handler.originalArtefact || handler.artefact);

		const mouseLeave = this.e('mouseout')
			.merge(reacquires)
			::handleBoxer('*') // TODO: <-- selectable?
			.map(handler => handler.originalArtefact || handler.artefact);

		const altScroll = this.rootE('mousewheel')
			.filter(withMod('alt'))
			.do((e) => { e.preventDefault() })
			.map(e=>e.deltaY);

		const mouseArtefact = mouseEnter.switchMap(enter => {

			const initialSelect = Observable.of(enter);

			const mouseleaveSelect = Observable.merge(
				mouseEnter.filter(next  => next  !== enter),
				mouseLeave.filter(leave => leave === enter).map(() => null)
			).take(1);

			const stack = [];
			const altScrollSelect = altScroll.scan((s, d) => {
				let next;
				if (d > 0) { // up
					if (stack[0] !== s) {
						stack.unshift(s);
						next = s.parent; // TODO: search upward by selection criteria (like supported handlers)
					}
				} else { // back down
					next = stack.shift();
				}
				if (!next) { return s }
				return next;
			}, enter);

			return Observable.merge(
				initialSelect,
				altScrollSelect,
				mouseleaveSelect.delay(1)
			);
		}).startWith(null);

		coach.newProperty('selectedArtefact', {
			source: mouseArtefact,
			allowSynchronousAccess: true
		});

		// coach.p('selectedArtefact').subscribe((a) => {
		// 	console.log('selected:', a && a.svg.main.attr('class'));
		// });


	}
}

import {ValueTracker, property} from 'utilities';
import {isUndefined}            from 'lodash-bound';
import {Observable}             from '../libs/expect-rxjs.js';


/**
 * The abstract `Tool` class is the basis of all boxer tools.
 */
export default class Tool extends ValueTracker {

	@property({ initial: true }) active;

	constructor({active} = {}) {
		super();
		if (!active::isUndefined()) { this.active = active }
	}

	init({coach, events = []} = {}) {
		this.coach = coach;
		for (let e of events) {
			coach.registerArtefactEvent(e);
		}
	}

	myE(stream)  { return this.p('active').switchMap(a => a ? stream : Observable.never()) }
	rootE    (e) { return this.myE(this.coach.rootE    (e)) }
	windowE  (e) { return this.myE(this.coach.windowE  (e)) }
	documentE(e) { return this.myE(this.coach.documentE(e)) }
	e        (e) { return this.myE(this.coach.e        (e)) }

}

import Tool from './Tool';
import {stopPropagation, withoutMod, property} from 'utilities';
import {multiply as _multiply} from 'lodash';

import {scaleFromPoint} from "../util/svg";

const {pow} = Math;


/**
 * A tool to zoom in and out on the main canvas using the scrollwheel,
 * centered around the current mouse-position.
 */
export class ZoomTool extends Tool {

	@property({ initial: 0.004             }) sensitivity;
	@property({ initial: 1, readonly: true }) factor;

	init({coach}) {
		super.init({coach});

		const mousewheel = this.rootE('mousewheel');

		const zooming = mousewheel
			.filter(withoutMod('alt', 'ctrl', 'meta'))
			.do(stopPropagation);

		/* maintain the current zoom-factor on the side (it doesn't actually influence zoom) */
		zooming
			.withLatestFrom(this.p('sensitivity'), ({deltaY: d}, s) => pow(1+s, d))
			.scan(_multiply, 1)
			.subscribe( this.pSubject('factor') );

		/* maintain zoom-exponent by mouse-wheel */
		zooming
			.withLatestFrom(coach.root.p('transformation'), this.p('sensitivity'),
				({originalEvent: {deltaY: d}, point}, m, s) => m::scaleFromPoint(pow(1+s, -d), point.in(coach.root.svg.children)))
			.subscribe( coach.root.p('transformation') );

	}

}

import {isArray, isUndefined, isString, toPairs} from 'lodash-bound';
import {ValueTracker, property} from 'utilities';


/**
 * A finite state machine in which state transitions are controlled by
 * observables which are only active in specific states.
 */
export default class Machine extends ValueTracker {

	/**
	 * the current state
	 * @type {string}
	 */
	@property({ initial: null }) state;

	/**
	 * the data belonging to the current state
	 * @type {*}
	 */
	@property({ initial: null }) data;

	/**
	 * the name of this state machine, used for log output
	 * @type {?string}
	 */
	name = null;

	/**
	 * the currently active (state-specific) observable subscriptions
	 * @type {Array<Rx.Subscription>}
	 * @private
	 */
	_subscriptions = [];

	/**
	 * Create a new finite state machine.
	 * @param {string} name - the name of the state machine
	 * @param {Object} options
	 * @param {string} options.state - the initial state
	 * @param {*}      options.data  - the initial state data
	 * @param {?Function|true} options.log   - the function to which to record log messages
	 */
	constructor(name, options) {
		super();
		const {state, data, log} = options;
		this.name  = name;
		this.state = state;
		this.data  = data;
		if (log === true)         { this.log = ::console.info }
		else if (log::isString()) { this.log = ::console[log] }
		else                      { this.log = ()=>{}         }
	}

	/**
	 * a description of how arrival to a certain state in some other machine leads to a specific state transition in this machine;
	 * the format is `['stateBefore', otherMachine.e('newState'), 'stateAfter']`
	 * @typedef {[string, Observable, string]} MachineLink
	 */

	/**
	 * Link a state transition in one machine to a state transition in another.
	 * @param {...(Array<MachineLink>|MachineLink)} links
	 * @return {Machine}
	 */
	link(...links) {
		if (!links[0]::isArray()) { links = [links] }
		for (let [localState1, otherState, localState2] of links) {
			this.extend(({ enterState }) => ({
				[localState1]: () => {
					otherState::enterState(localState2)
				}
			}));
		}
		/* return machine itself */
		return this;
	}

	/**
	 * Extend this machine with additional states, behavior and/or transitions.
	 * @param {Function} descFn - a function that should return an object mapping state-names
	 *                            to functions that may (asynchronously, conditionally) initiate
	 *                            new state transitions in turn. It is passed an object with the
	 *                            `enterState` and `subscribeDuringState`. Use the former to enter
	 *                            new states (can also be bound to an observable). Bind the latter
	 *                            to an `Observable` instead of using `.subscribe` from within a
	 *                            machine state, so it is automatically unsubscribed at a state-
	 *                            transition.
	 * @param {function (): boolean} [runCondition] - a function that should dynamically indicate
	 *                                                whether the machine is currently active;
	 *                                                if omitted, assumes the machine is always active
	 */
	extend(descFn, runCondition = ()=>true) {
		/* define bound convenience functions */
		const thisMachine = this;
		const boundFunctions = {
			enterState(nextState, data) {
				if (this::isUndefined()) {
					return thisMachine.enterState(nextState, data);
				} else {
					return this::(boundFunctions.subscribeDuringState)((data) => {
						thisMachine.enterState(nextState, data);
					});
				}
			},
			subscribeDuringState(...args) {
				const sub = this.subscribe(...args);
				thisMachine._subscriptions.push(sub);
				return sub;
			}
		};
		/* extend descriptions */
		let result = descFn(boundFunctions);
		let subscribers = [];
		for (let [key, fn] of result::toPairs()) {
			if (this[key]::isUndefined()) {
				this[key] = this.newEvent(key);
			}
			const subscriber = this.e(key).filter(runCondition).subscribe((data) => {
				this._runStateSpecificCode(fn);
			});
			subscribers.push(subscriber);
		}
		/* run if it says something about the current state */
		if (this.state in result && runCondition()) {
			this._runStateSpecificCode(result[this.state]);
		}
		/* return a way to undo the extension */
		return () => {
			for (const sub of subscribers) {
				sub.unsubscribe();
			}
		};
	}

	/**
	 * Unsubscribe from all currently registered `Observable` subscriptions.
	 */
	unsubscribe() {
		for (let sub of this._subscriptions.reverse()) {
			sub.unsubscribe();
		}
		this._subscriptions = [];
	}

	/**
	 * Enter a new machine state.
	 * @param {string} state - the new state
	 * @param {*}      data  - data to send to the new state
	 */
	enterState(state, data) {
		this.unsubscribe();
		this.log(`${this.name} - entering state: '${state}'`, [data]);
		[this.state, this.data] = [state, data];
		this.e(state).next(data);
	}

	/**
	 * Run the code associated with transition to some specific state.
	 * Running it through this method keeps track of subscriptions returned from it.
	 * @param {function(*):(void|Rx.Subscription|Array<Rx.Subscription>)} specifiedStateFn - the code to run
	 * @private
	 */
	_runStateSpecificCode(specifiedStateFn) {
		let newSubscriptions = specifiedStateFn(this.data);
		if (newSubscriptions::isUndefined()) { newSubscriptions = []                 }
		if (!newSubscriptions::isArray())    { newSubscriptions = [newSubscriptions] }
		this._subscriptions.push(...newSubscriptions);
	}

};

